sss = ''


def fontFile(base64_string: str):
    bin_data = base64.decodebytes(base64_string.encode())
    with open('text.otf', 'wb') as f:
        f.write(bin_data)
    return bin_data


def font2xml(bin_data):
    font = TTFont(BytesIO(bin_data))
    font.saveXML("text.xml")

# 解析字体
bin_data = fontFile(sss)
font2xml(bin_data)

